mapper.select:
SELECT um.nickname ,t1.comment_counts,t1.gmt_create,t1.id, t1.summary,t1.title,t1.tag,t1.view_counts,t1.weight FROM atai_article t1, ucenter_member um
t1.author_id=um.id and t1.is_deleted=0
and t1.title like "%"#{articleQuery.title,jdbcType=VARCHAR}"%"
and t1.gmt_create > #{articleQuery.begin}
and t1.gmt_create < #{articleQuery.end}
order by t1.gmt_modified desc

mapper.update:
UPDATE atai_article
SET view_counts=view_counts + 1
WHERE id = #{id}

UPDATE atai_article
SET comment_counts=comment_counts + 1
WHERE id = #{id}
